import React, { useState, useEffect } from 'react';
import { Bar, Line } from 'react-chartjs-2';
import 'chart.js/auto';
import logo from './logo.svg';
import './App.css';

function MyForm() {
  const [idUsuario, setIdUsuario] = useState('');
  const [descricao, setDescricao] = useState('');
  const [categoria, setCategoria] = useState('');
  const [valor, setValor] = useState('');
  const [data, setData] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault(); // Impede o comportamento padrão do formulário de recarregar a página

    fetch('http://localhost:8080/add-gasto', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ 
        usuario_id: parseInt(idUsuario), 
        descricao: descricao,
        categoria: categoria,
        valor: parseFloat(valor),
        data_gasto: data
      }),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
        // Adicione aqui qualquer lógica que você queira ao receber a resposta
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        id do usuario:
        <input
          type="number"
          value={idUsuario}
          onChange={(e) => setIdUsuario(e.target.value)}
        />
        descricao:
        <input
          type="descricao" // Altere o type para password para ocultar a senha
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
        />
        categoria:
        <input
          type="categoria" // Altere o type para password para ocultar a senha
          value={categoria}
          onChange={(e) => setCategoria(e.target.value)}
        />
        valor:
        <input
          type="number" // Altere o type para password para ocultar a senha
          value={valor}
          onChange={(e) => setValor(e.target.value)}
        />
        data:
        <input
          type="data" // Altere o type para password para ocultar a senha
          value={data}
          onChange={(e) => setData(e.target.value)}
        />
      </label>
      <button type="submit">Enviar</button>
    </form>
  );
}

function AreaChart() {
  const [chartData, setChartData] = useState({ labels: [], datasets: [] });

  useEffect(() => {
    const usuarioId = 1; // ID do usuário específico

    fetch(`http://localhost:8080/api/gastos/${usuarioId}`)
      .then(response => response.json())
      .then(data => {
        const labels = data.map(gasto => gasto.data_gasto); // Assumindo que os dados possuem a propriedade `data_gasto`
        const values = data.map(gasto => gasto.valor); // Assumindo que os dados possuem a propriedade `valor`

        setChartData({
          labels: labels,
          datasets: [{
            label: 'Gastos',
            data: values,
            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)'
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)'
            ],
            borderWidth: 8,
            pointRadius: 8,
            pointHoverRadius: 12,
            fill: false,
          }]
        });
      })
      .catch(error => console.error('Error:', error));
  }, []);

  const options = {
    scales: {
      y: {
        beginAtZero: true
      }
    }
  };

  return (
    <div>
      <Line data={chartData} options={options} />
    </div>
  );
}


function App() {
  return (
    <div className="App">
      <header></header>
      <MyForm />
      <AreaChart />

    </div>
  );
}

export default App;
